import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { UserService } from '../services/user/user.service';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-cadastro',
  templateUrl: './cadastro.component.html',
  styleUrls: ['./cadastro.component.scss']
})
export class CadastroComponent implements OnInit {
  form: FormGroup
  formEnviado: boolean = false;

  message(icon: any, message: string){
    const Toast = Swal.mixin({
      toast: true,
      position: 'top-end',
      showConfirmButton: false,
      timer: 3000,
      timerProgressBar: true,
      didOpen: (toast) => {
        toast.addEventListener('mouseenter', Swal.stopTimer)
        toast.addEventListener('mouseleave', Swal.resumeTimer)
      }
    })

    Toast.fire({
      icon: icon,
      title: message
    })
  }


  constructor(
    private fb: FormBuilder,
    private userService: UserService
    ) {
    this.form = this.fb.group({
      nome: ['', [Validators.required ]],
      email: ['', [Validators.required, Validators.email]],
      senha: ['', [Validators.required]]
    })
   }

  ngOnInit() {
  }

  onSubmit(){
    this.formEnviado = true;
    console.log(this.form.value)
    this.userService.novoUsuario(this.form.value).subscribe(
      res => this.message('success', 'Conta Criada com Sucesso!'),
      error => this.message('error', 'Opps, Houve um Problema')
    )
  }
}
<div class="container" >
  <a class="links" id="paracadastro"></a>
  <a class="links" id="paralogin"></a>

  <div class="content">

    <div id="cadastro">
      <form [formGroup]="form">
        <h1>Cadastro</h1>

        <p>
          <label for="nome_cad">Seu nome</label>
          <input id="nome_cad" name="nome_cad" type="text" placeholder="Luiz Augusto" formControlName="nome"/>
        </p>

        <p>
          <label for="email_cad">Seu e-mail</label>
          <input id="email_cad" name="email_cad"  type="email" placeholder="contato@htmlecsspro.com" formControlName="email"/>
        </p>

        <p>
          <label for="senha_cad">Sua senha</label>
          <input id="senha_cad" name="senha_cad"  type="password" placeholder="1234" formControlName="senha"/>
        </p>


          <input type="button" [disabled]="!form.valid" value="Cadastrar" (click)="onSubmit()"/>


        <p class="link">
          Já tem conta?
          <a routerLink=""> Ir para Login </a>
        </p>
      </form>
    </div>
  </div>
</div>
