import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { UserService } from '../services/user/user.service';
import { Router } from '@angular/router';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
})
export class LoginComponent implements OnInit {
  form!: FormGroup;

  constructor(
    private user: UserService,
    private fb: FormBuilder,
    private router: Router
  ) {
    this.form = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      senha: ['', [Validators.required]],
    });
  }

  ngOnInit() {}

  teste() {
    const Toast = Swal.mixin({
      toast: true,
      position: 'top-end',
      showConfirmButton: false,
      timer: 3000,
      timerProgressBar: true,
      didOpen: (toast) => {
        toast.addEventListener('mouseenter', Swal.stopTimer);
        toast.addEventListener('mouseleave', Swal.resumeTimer);
      },
    });

    Toast.fire({
      icon: 'error',
      title: 'Usuario ou senha Invalidos!',
    });
  }

  onSubmit() {
    if (this.form.invalid) {
      return;
    }
    this.user.autenticarUsuario().subscribe((res) => {
      console.log(res, 'ola');
      const filtered = res.filter((t: any) => {
        return (
          this.form.value.email === t.email &&
          this.form.value.senha === t.senha
        );
      });
      if (filtered.length > 0) {
        this.router.navigate(['tabela']);
      } else {
        this.teste();
      }
    });
  }
}
<section class="boxLogin container">
  <div class="content">

        <form [formGroup]="form" class="login" method="post" name="login">

          <h1><i class="icon icon-key-1"></i> Login</h1>

          <div class="padding">

            <label>
              <input type="text" class="campos" placeholder="E-mail" name="email" formControlName="email">
            </label>

            <label>
              <input type="password" class="campos" placeholder="Password"  name="senha" formControlName="senha">
            </label>

            <input (click)="onSubmit()" type="submit" class="btn fade_8S" name="login" value="Entrar">

          </div><!--padding-->

          <div class="linksForm">

            <a class="fade_4S" title="Resgatar Senha"><i class="icon icon-forward-1"></i> Resgatar Senha</a>
            <a routerLink="cadastro" class="fade_4S" title="Cadastrar-se"><i class="icon icon-user-add"></i> Cadastro</a>

          </div>

        </form>

   </div>
   </section>
